import React,{useRef} from 'react';
import Navbar from './Navbar';
import Header from './Header';
import AboutMe from './AboutMe';
import Projects from './Projects';
import Timeline from './Timeline';



const Home = () => {
    const service = useRef(null);
    const works = useRef(null);
    const contact = useRef(null);
    const scrollToSection= (elementRef) =>{
        console.log(elementRef,'lll')
        window.scrollTo({
            top:elementRef.current.offsetTop,
            behavior:"smooth",
        });
    };  

    const serviceGo = scrollToSection(service)
    const worksGo = scrollToSection(works)
    const contactGo = scrollToSection(contact)
    return (
        <React.Fragment>
            <Navbar   service={scrollToSection(service)}
            works={ scrollToSection(works)} contact={ scrollToSection(contact)}
            />
            <Header/>
            <AboutMe/>
            <Timeline ref={service}/>
            <Projects ref={works}/>
       </React.Fragment>
    );
};

export default Home;
