import React,{useState} from 'react';
import {Box,Container,Grid,useMediaQuery} from '@mui/material';
import "./timeline.css";
import { styled } from '@mui/material/styles';
import { useTheme } from '@mui/material/styles';
import MobileStepper from '@mui/material/MobileStepper';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import SwipeableViews from 'react-swipeable-views';
import { autoPlay } from 'react-swipeable-views-utils';
import { steps } from '../utils/commonHelper';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepButton from '@mui/material/StepButton';
import Fade from 'react-reveal/Fade';
import Time from './time';
import Masonry from '@mui/lab/Masonry';
const AutoPlaySwipeableViews = autoPlay(SwipeableViews);


const Label = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(0.5),
  textAlign: 'center',
  color: theme.palette.text.secondary,
  borderBottomLeftRadius: 0,
  borderBottomRightRadius: 0,
}));



export default function TextMobileStepper() {
  const theme = useTheme();
  const [isShown, setIsShown] = useState(false);
  const isSxMatch = useMediaQuery(theme.breakpoints.down("xs"));
  const isSmMatch = useMediaQuery(theme.breakpoints.down("sm"));
  const isMdMatch = useMediaQuery(theme.breakpoints.down("md"));
  const isLgMatch = useMediaQuery(theme.breakpoints.down("lg"));
  const [activeStep, setActiveStep] = React.useState(0);
  const [completed, setCompleted] = React.useState({});
  const [skipped, setSkipped] = React.useState(new Set());
  const maxSteps = steps.length;
  const totalSteps = () => {
    return steps.length;
  };

  const completedSteps = () => {
    return Object.keys(completed).length;
  };
  const isStepOptional = (step) => {
    return step === 1;
  };

  const isStepSkipped = (step) => {
    return skipped.has(step);
  };
  const isLastStep = () => {
    return activeStep === totalSteps() - 1;
  };

  const allStepsCompleted = () => {
    return completedSteps() === totalSteps();
  };
  const handleNext = () => {
   let newSkipped = skipped;
    if (isStepSkipped(activeStep)) {
      newSkipped = new Set(newSkipped.values());
      newSkipped.delete(activeStep);
    }

    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };
  const handleStep = (step) => () => {
    setActiveStep(step);
  };

  const handleComplete = () => {
    const newCompleted = completed;
    newCompleted[activeStep] = true;
    setCompleted(newCompleted);
    handleNext();
  };
  const handleStepChange = (step) => {
    setActiveStep(step);
  };
  const handleReset = () => {
    setActiveStep(0);
    setCompleted({});
  };
  return (
    <Container maxWidth={false} disableGutters sx={{ background:"#303131"}}>
 <Container  sx={{paddingTop:10}}>

 <Fade left>
 <Box sx={{ typography: isLgMatch?'h5':'h3', color: '#fff',textAlign: 'center',paddingBottom:3}} >Projects</Box>
  </Fade>{/* <Time/> */}
  <Fade right>
  {/* <Box sx={{ width: 500, minHeight: 829 }}>
      <Masonry columns={3} spacing={2}>
        {steps.map((item, index) => (
          <div onClick={handleStep(index)}>
          <div key={index}>
            <Label>{index + 1}</Label>
            <img
              src={`${item.imgPath}?w=162&auto=format`}
              srcSet={`${item.imgPath}?w=162&auto=format&dpr=2 2x`}
              alt={item.label1}
              loading="lazy"
              style={{
                borderBottomLeftRadius: 4,
                borderBottomRightRadius: 4,
                display: 'block',
                width: '100%',
              }}
            />
          </div>
          </div>
        ))}
      </Masonry>
    </Box> */}
    <Box>
  <Box sx={{ width: '100%' }}>
      <Stepper nonLinear activeStep={activeStep}>
        {steps.map((label, index) => {
           const stepProps = {};
           const labelProps = {};
           if (isStepOptional(index)) {
             labelProps.optional = (
               <Typography variant="caption">Optional</Typography>
             );
           }
           if (isStepSkipped(index)) {
             stepProps.completed = false;
           }
           return (
          <Step key={label.label1} {...stepProps} completed={completed[index]}>
            <StepButton color="inherit" onClick={handleStep(index)}>
              {/* {label} */}
              <label.icon/>
              {/* {label.label1} */}
              {/* <Box
                component="img"
                sx={{
                  width:'100%',
                  minHeight: 60,
    minWidth: 100,
    maxWidth: 100,
    maxHeight: 60,
                  // border: '3px solid #f5af31',
                }}
                src={label.imgPath}
                alt={label.label1}
              /> */}
            </StepButton>
          </Step>
        )})}
      </Stepper>

    </Box>
 <Box sx={{
                    flexGrow: 1,  
                }}>
                    
                    <Grid container spacing={2} className='checkGriud' onMouseEnter={() => setIsShown(true)}
        onMouseLeave={() => setIsShown(false)}>
                    {isShown && (
                    <div className='backBut'>
    <Button  size="small" onClick={handleBack} sx={{color:'#fff'}} disabled={activeStep === 0}>
              {theme.direction === 'rtl' ? (
                <KeyboardArrowRight />
              ) : (
                <KeyboardArrowLeft />
              )}
            </Button></div>)}
                    
                        <Grid  container spacing={0} 
                            direction="column" item xs={12} lg={6}
                             sx={{placeSelf: 'flex-start',display:'flex', }}>
                               {/* <MobileStepper 
                               sx={{background: '#f5af31'}}
        variant="text"
        steps={maxSteps}
        position="static"
        activeStep={activeStep}
        nextButton={
          <Button
            size="small"
            onClick={handleNext}
            disabled={activeStep === maxSteps - 1}
            sx={{color:'#fff'}}
          >
            Next
            {theme.direction === 'rtl' ? (
              <KeyboardArrowLeft />
            ) : (
              <KeyboardArrowRight />
            )}
          </Button>
        }
        backButton={
          <Button size="small" onClick={handleBack} sx={{color:'#fff'}} disabled={activeStep === 0}>
            {theme.direction === 'rtl' ? (
              <KeyboardArrowRight />
            ) : (
              <KeyboardArrowLeft />
            )}
            Back
          </Button>
        }
      />  */}
                              <Box sx={{ width:'100%'}}>
                            <SwipeableViews
        axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
        index={activeStep}
        onChangeIndex={handleStepChange}
        enableMouseEvents
      >
        {steps.map((step, index) => (
          <div key={step.label}>
            {Math.abs(activeStep - index) <= 2 ? (
                <>
                
              <Box
                component="img"
                sx={{
                  width:'100%',
                  // border: '3px solid #f5af31',
                }}
                src={step.imgPath}
                alt={step.label}
              /> 
              {/* <Box sx={{  maxWidth: 500, width: '100%',paddingTop:2 ,color:'#a8a8a8'}}>
              {step.description1}
            </Box> */}
            </>
            ) : null}
            
          </div>
        ))}
        
      </SwipeableViews></Box>

                        </Grid>
                        <Grid container
                            spacing={0}
                            direction="column"
                            style={{
                                justifyContent: "center", alignContent: 'center',
                            }} item xs={12} lg={6}>
                                
                                <Box sx={{ maxWidth: 400, flexGrow: 1 }}>
      <Paper
        square
        elevation={0}
        sx={{
          display: 'flex',
          bgcolor: 'transparent',
          paddingBottom:2
        }}
      >
        <Typography  sx={{ typography: isLgMatch?'h6':'h5', color: '#fff',paddingLeft:0 }}   >{steps[activeStep].label1}</Typography>
      </Paper>
      <Box sx={{  maxWidth: 500, width: '100%' ,color:'#a8a8a8'}}>
              {steps[activeStep].description1}
            </Box>
      {/* <Box sx={{ maxWidth: 400, width: '100%',color:'#a8a8a8'  }}>
        {steps[activeStep].description2}</Box>
        <Typography sx={{paddingTop:2,paddingBottom:2,color:'#fff'}}>{steps[activeStep].label3}</Typography>
      <Box sx={{ maxWidth: 400, width: '100%',color:'#a8a8a8' }}>
        {steps[activeStep].description3}
      </Box> */}

    </Box>    </Grid>
    {isShown && (
    <div className='nextBut'>
    
    <Button 
            size="small"
            onClick={handleNext}
            disabled={activeStep === maxSteps - 1}
            sx={{color:'#fff'}}
          >
            {theme.direction === 'rtl' ? (
              <KeyboardArrowLeft />
            ) : (
              <KeyboardArrowRight />
            )}
          </Button></div>)}

                    </Grid>
                   
                </Box></Box></Fade>
   </Container></Container>
  );
}
