import React from 'react';
import { Container, Grid, Box, styled, Paper, CardMedia, useMediaQuery, useTheme, Button } from '@mui/material';
import DownloadIcon from '@mui/icons-material/Download';
import innerjpg from '../assets/055A7430.jpeg'

function AboutMe(props) {
    const theme = useTheme();
    const isSxMatch = useMediaQuery(theme.breakpoints.down("xs"));
    const isSmMatch = useMediaQuery(theme.breakpoints.down("sm"));
    const isMdMatch = useMediaQuery(theme.breakpoints.down("md"));
    const isLgMatch = useMediaQuery(theme.breakpoints.down("lg"));

    let fontSize
    return (

        <React.Fragment>

            <Container maxWidth={false} disableGutters sx={{ backgroundColor: '#303131' }}>
                <Container>
                <Box sx={{
                    flexGrow: 1,
                    paddingTop: isLgMatch ? "20%" : 'auto',paddingTop:'7%',paddingBottom:'5%',
                    
                }}>
                    <Grid container spacing={2}>
                        <Grid spacing={0}
                            direction="column" item xs={12} lg={6} sx={{placeSelf: 'center',display:'flex' }}>
                            <Box sx={{
                                margin: '0px auto',
                                width: '25%',
                                border: '4px solid #f5af31',
                                padding: '10px 10px 20px 25px'
                            }}>
                                <CardMedia
                                    component="img"
                                    sx={{
                                        width: isLgMatch ? '100%' : 'auto',
                                        objectFit: 'cover',
                                        height: 350,
                                        border: '10px solid #444',
                                        marginTop: -5
                                    
                                    }}
                                image={isLgMatch ? innerjpg : innerjpg}
                                alignItems='right'
                                alt="Live from space album cover"
              /></Box>
                        </Grid>
                        <Grid container
                            spacing={0}
                            direction="column"
                            textAlign={isLgMatch ? 'center' : 'left'}
                            style={{
                                justifyContent: "center", padding: "2% 2%", alignContent: 'center',
                                alignItems: isLgMatch ? 'center' : 'auto'
                            }} item xs={12} lg={6}>
                                 <Box sx={{ typography: isLgMatch?'h5':'h3', color: '#fff' }}>About Me</Box>
                           <Box sx={{ typography: 'p', color: '#a8a8a8' }}>I am skilled in React Native , React.Js , Javascript , HTML , CSS  have worked with both web-based, multi-threaded applications as well as front-end development using the latest web technologies and good in developing customer service-oriented applications with solid test automation strategies.

In past have worked on both service and project side in service projects utilizing  Blockchain technologies like Trade , Swap ,Liquidity , stake , NFT and in product game platform like Dream11 ,e-commerce moreover in React Native educational based app for students ,music app like Gaana.

</Box> <Box sx={{ typography: 'p', color: '#a8a8a8' }}>
In addition to my skills in coding, I am skilled in conducting technical analysis as well as assisting clients in project management.

Software Engineer | React Developer | React Native Developer | Web Designer , Web Developer</Box>
                        </Grid>



                    </Grid>
                </Box>
                </Container>
            </Container>

        </React.Fragment>
    );
}

export default AboutMe;
