import React, { useState, useEffect } from 'react';
import { Box, Container, Grid, useMediaQuery, Modal, Button, Typography, CardActions, Card, CardContent, CardMedia } from '@mui/material';
import { useTheme } from '@mui/material/styles';
import Fade from 'react-reveal/Fade';
import hire from '../assets/hire_bg.png'
import stackBg from '../assets/stackBg.png'
import reactlogo from '../assets/react-seeklogo.com.svg'
import reactNativelogo from '../assets/react-native-seeklogo.com.svg'
import reduxlogo from '../assets/redux.png'
import Zoom from 'react-reveal/Zoom';
import Slide from 'react-reveal/Slide';
export const stacks = [
  {
    id: 0,
    icon: reactlogo, // 'https://images.unsplash.com/photo-1537944434965-cf4679d1a598?auto=format&fit=crop&w=400&h=250&q=60',
  },
  {
    id: 1,
    icon: reactNativelogo,
  },
  {
    id: 2,
    icon: reduxlogo,
  },
 
];

export default function Contact() {
  const theme = useTheme();
  const isSxMatch = useMediaQuery(theme.breakpoints.down("xs"));
  const isSmMatch = useMediaQuery(theme.breakpoints.down("sm"));
  const isMdMatch = useMediaQuery(theme.breakpoints.down("md"));
  const isLgMatch = useMediaQuery(theme.breakpoints.down("lg"));

  return (
    <Container maxWidth={false} disableGutters sx={{ background: "#303131", 
    // backgroundImage: `url(${headerjpg})`
     }}>
    <Container maxWidth={false} disableGutters 
    // sx={{ backgroundImage: `url(${hire})`, backgroundAttachment: 'fixed' }}
    >
        <Container sx={{ paddingTop: 5, paddingBottom: 5 }}>

          <Fade left>
            <Box sx={{ typography: isLgMatch ? 'h5' : 'h3', color: '#fff', textAlign: 'center', paddingBottom: 10 }} >Work Expertise</Box>
          </Fade>{/* <Time/> */}
        
          
            <Box sx={{
              flexGrow: 1,
            }}>  <Grid container spacing={2} className='checkGriud'
                 
              >



                <Grid container spacing={0}

                  className='expertise'
                  direction="column" item xs={12} lg={5} sm={6} md={6}
                  sx={{ placeSelf: 'center', display: 'flex', }}>
                    <Slide bottom >
                  <Box >
                    <Box sx={{

                      margin: '0px auto',
                      width: isSmMatch ? '95%' : '50%',
                    }}>

                      <CardMedia
                        component="img"
                        sx={{
                          width: isLgMatch ? '100%' : 'auto',
                          objectFit: 'cover',
                          height: 350,
                          border: '10px solid #f5af31',
                          background: '#fff'

                        }}
                        image={isLgMatch ? stackBg : stackBg}
                        alignItems='right'
                        alt="Live from space album cover"
                      /></Box>
                  </Box>
                  </Slide>
                </Grid>
               
                <Grid container spacing={0}

                  className='expertise'
                  direction="column" item xs={12} lg={7} sm={6} md={6}
                  sx={{ placeSelf: 'center', display: 'flex' }}>


                      <Grid container spacing={2} sx={{justifyContent:'center'}}
            >
              <Zoom >
                     {stacks.map((img,i)=>(
                     <Grid container spacing={0}

                     className='expertiseLogo'
                     direction="column" item xs={12} lg={2} sm={3} md={3}
                     sx={{ placeSelf: 'center', display: 'flex'  }}
                 >
                  <CardMedia
                    component="img"
                    sx={{
                      width: 60,
                      objectFit: 'contain',
                      height: 60,
                      background: '#2a2a2a',
                      padding: 2,
                      margin:1,
                      borderRadius:50 ,
                      boxShadow: '0 0.25em 0.5em 0 rgb(0 0 0 / 25%), 0 0.4em 1.25em 0 rgb(0 0 0 / 15%)',
                   

                    }}
                    image={img.icon}
                    alignItems='right'
                    alt="Live from space album cover"
                  /></Grid>))} </Zoom> </Grid>  
                </Grid>
              </Grid>
            </Box>


        </Container>

      </Container></Container>
  );
}
