import * as React from 'react';
import PropTypes from 'prop-types';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { steps } from '../utils/commonHelper';

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`vertical-tabpanel-${index}`}
      aria-labelledby={`vertical-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `vertical-tab-${index}`,
    'aria-controls': `vertical-tabpanel-${index}`,
  };
}

export default function VerticalTabs() {
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Box
      sx={{ flexGrow: 1, bgcolor: 'background.paper', display: 'flex', height: 224 }}
    >
      <Tabs
        orientation="vertical"
        variant="scrollable"
        value={value}
        onChange={handleChange}
        aria-label="Vertical tabs example"
        sx={{ borderRight: 1, borderColor: 'divider' }}
      >
         {steps.map((step, index) => (
        <Tab label={step.label1} {...a11yProps(step.id)} />))}
      </Tabs>
      {steps.map((step, index) => (
      <TabPanel value={value} index={step.id}>
         <Box sx={{ width:300,heigh:300}}>
         <Box
                component="img"
                sx={{
                  width:'100%',
                  height:'100%'
                  // border: '3px solid #f5af31',
                }}
                src={step.imgPath}
                alt={step.label}
              /> </Box>
               <Typography  sx={{ typography: 'h5', color: '#fff',paddingLeft:0 }}   >{step.label1}</Typography>
      <Box sx={{  maxWidth: 500, width: '100%',paddingTop:2 ,color:'#a8a8a8'}}>
              {step.description1}
            </Box>
      </TabPanel>))}
      
    </Box>
  );
}
