import React, { useState,useEffect } from 'react';
import { Box, Container, Grid, useMediaQuery, Modal, Button, Typography, CardActions, Card, CardContent, CardMedia } from '@mui/material';
import "./timeline.css";
import { styled } from '@mui/material/styles';
import { useTheme } from '@mui/material/styles';
import MobileStepper from '@mui/material/MobileStepper';
import Paper from '@mui/material/Paper';
import headerjpg from '../assets/allbg.webp'
import OpenInNewIcon from '@mui/icons-material/OpenInNew';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import SwipeableViews from 'react-swipeable-views';
import { autoPlay } from 'react-swipeable-views-utils';
import { steps } from '../utils/commonHelper';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepButton from '@mui/material/StepButton';
import Fade from 'react-reveal/Fade';
import Time from './time';
import Masonry from '@mui/lab/Masonry';
const AutoPlaySwipeableViews = autoPlay(SwipeableViews);


const Label = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(0.5),
  textAlign: 'center',
  color: theme.palette.text.secondary,
  borderBottomLeftRadius: 0,
  borderBottomRightRadius: 0,
}));

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

export default function TextMobileStepper() {
  const theme = useTheme();
  const [isShown, setIsShown] = useState(false);
  const [getID, setGedID] = useState(0);
  const isSxMatch = useMediaQuery(theme.breakpoints.down("xs"));
  const isSmMatch = useMediaQuery(theme.breakpoints.down("sm"));
  const isMdMatch = useMediaQuery(theme.breakpoints.down("md"));
  const isLgMatch = useMediaQuery(theme.breakpoints.down("lg"));
  const [open, setOpen] = React.useState(false);
  const handleOpen = (id) => {
    setGedID(id)
    setOpen(true)};
  const handleClose = () => setOpen(false);
  const [activeStep, setActiveStep] = React.useState(0);
  const [completed, setCompleted] = React.useState({});
  const maxSteps = steps.length;
useEffect(()=>{
  console.log(getID,'egtSteps')
},[getID])
  
  const totalSteps = () => {
    return steps.length;
  };

  const completedSteps = () => {
    return Object.keys(completed).length;
  };

  const isLastStep = () => {
    return activeStep === totalSteps() - 1;
  };

  const allStepsCompleted = () => {
    return completedSteps() === totalSteps();
  };
  const handleNext = () => {
    const newActiveStep =
      isLastStep() && !allStepsCompleted()
        ? // It's the last step, but not all steps have been completed,
        // find the first step that has been completed

        steps.findIndex((step, i) => !(i in completed))
        : activeStep + 1;
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };
  const handleStep = (step) => () => {
    setActiveStep(step);
  };

  const handleComplete = () => {
    const newCompleted = completed;
    newCompleted[activeStep] = true;
    setCompleted(newCompleted);
    handleNext();
  };
  const handleStepChange = (step) => {
    setActiveStep(step);
  };
  const handleReset = () => {
    setActiveStep(0);
    setCompleted({});
  };
  return (
    <Container maxWidth={false} disableGutters sx={{ background: "#303131", backgroundImage: `url(${headerjpg})` }}>
      <Container sx={{ paddingTop: 10, paddingBottom: 10 }}>

        <Fade left>
          <Box sx={{ typography: isLgMatch ? 'h5' : 'h3', color: '#fff', textAlign: 'center', paddingBottom: 3 }} >Projects</Box>
        </Fade>{/* <Time/> */}
        <Fade right>

          <Box sx={{
            flexGrow: 1,
          }}>

            <Grid container spacing={2} className='checkGriud'
            >

              {steps.slice(0, 6).map((step, index) => (

                <Grid container spacing={0}

                  className='singlephoto'
                  direction="column" item xs={12} lg={4}
                  sx={{ placeSelf: 'flex-start', display: 'flex' }}>
                  <Card sx={{ maxWidth: 345, }}>
                    <step.icon className='iconMangement' />
                    <CardContent>
                      <Typography gutterBottom variant="h5" component="div">
                        {step.label1}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {step.description1.slice(0, 120) + '...'
                        }
                      </Typography>
                    </CardContent>
                    <Button  onClick={()=>handleOpen(step.id)} className='buttonProject' variant="contained" endIcon={<OpenInNewIcon />}>Read More</Button>

                  </Card>

                </Grid>))}
            </Grid>
          </Box></Fade>
          
          <Modal
        keepMounted
        open={open}
        classes
        onClose={handleClose}
        aria-labelledby="keep-mounted-modal-title"
        aria-describedby="keep-mounted-modal-description"
      >
        <Box sx={style}>
          
        <Box sx={{ flexGrow: 1 }}>
        <Grid container spacing={2} className='checkGriud'
            >

                <Grid container spacing={0}

                  className='singlephoto'
                  direction="column" item xs={12} lg={6}
                  sx={{ placeSelf: 'flex-start', display: 'flex' }}>
        <Box
                component="img"
                sx={{
                  width:'100%',
                  borderRadius: 2,
                  // border: '3px solid #f5af31',
                }}
                src={steps[getID].imgPath}
                alt={steps.label}
              />
               <Box sx={{   width: '100%',paddingTop:2 ,color:'#a8a8a8'}}>
              {steps[getID].description1}
            </Box>
              <Typography sx={{paddingTop:2,paddingBottom:2,color:'#f5af31'}}>{steps[getID].label3}</Typography>
      <Box sx={{  width: '100%',color:'#a8a8a8' }}>
        {steps[getID].description3}
      </Box> </Grid>
      {/* <Paper
        square
        elevation={0}
        sx={{
          display: 'flex',
          bgcolor: 'transparent',
          paddingBottom:2
        }}
      >
        <Typography  sx={{ typography: isLgMatch?'h6':'h5', color: '#fff',paddingLeft:0 }}   >{steps[getID].label1}</Typography>
      </Paper> */}
      <Grid container spacing={0}

className='singlephoto'
direction="column" item xs={12} lg={6}
sx={{ placeSelf: 'flex-start', display: 'flex' }}>
      <Typography sx={{paddingBottom:2,color:'#f5af31'}}>{steps[getID].label3}</Typography>
                 <Box sx={{ width: '100%',color:'#a8a8a8'  }}>
        {steps[getID].description2}</Box>
        </Grid></Grid>
        </Box>
        </Box>
      </Modal>
          

      </Container></Container>
  );
}
