import { ReactComponent as WorkIcon } from "./work.svg";

import {
  VerticalTimeline,
  VerticalTimelineElement,
} from "react-vertical-timeline-component";

import "react-vertical-timeline-component/style.min.css";

function Timeline() {
  let workIconStyles = { background: "#06D6A0" };
  let schoolIconStyles = { background: "#f9c74f" };

  let timelineElements = [
    {
      id: 1,
      title: "Senior Programmer",
      location: "Osiz Tenchnologies",
      description:
        "As senior programmer managed team with multiple projects on web and mobile appication, handle blockchain and NFT concepts with smart contracts integration end to end.",
      date: "April 2021 ",
      icon: "school",
    },
    {
      id: 2,
      title: "Front End Developer",
      location: "Maventricks Technologies",
      description:
        "Worked as  Front End Developer focus on the visual layout, user interface/interaction as a part of various platforms like bidding , e-commerce , reqruiter websites and educational,service booking app in mobile developement  ",
      // buttonText: "College Projects",
      date: "November 2017 - March 2021",
      icon: "school",
    },
  ];
  
  
  return (
    <div className="timePort"  
    // style={{ backgroundImage: `url(${headerjpg})`}}
     >
      <h5 className="title">Experience</h5>
      <VerticalTimeline>
        {timelineElements.map((element) => {
          let isWorkIcon = element.icon === "work";
          let showButton =
            element.buttonText !== undefined &&
            element.buttonText !== null &&
            element.buttonText !== "";

          return (
            <VerticalTimelineElement
              key={element.key}
              date={element.date}
              dateClassName="date"
              iconStyle={isWorkIcon ? workIconStyles : schoolIconStyles}
              icon={isWorkIcon ? <WorkIcon /> : <WorkIcon />}
            >
              <h3 className="vertical-timeline-element-title">
                {element.title}
              </h3>
              <h5 className="vertical-timeline-element-subtitle">
                {element.location}
              </h5>
              <p id="description">{element.description}</p>
              {showButton && (
                <a
                  className={`button ${
                    isWorkIcon ? "workButton" : "schoolButton"
                  }`}
                  href="/"
                >
                  {element.buttonText}
                </a>
              )}
            </VerticalTimelineElement>
          );
        })}
      </VerticalTimeline>
    </div>
  );
}

export default Timeline;
