import React, { useState, useRef } from 'react';
import { Box, Container, Grid,TextField, useMediaQuery, Modal, Button, Typography, CardActions, Card, CardContent,Link, CardMedia } from '@mui/material';
import { useTheme } from '@mui/material/styles';
import Fade from 'react-reveal/Fade';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import MailIcon from '@mui/icons-material/Mail';
import CallIcon from '@mui/icons-material/Call';
import reactlogo from '../assets/react-seeklogo.com.svg'
import reactNativelogo from '../assets/react-native-seeklogo.com.svg'
import reduxlogo from '../assets/redux.png'
import { useForm } from "react-hook-form";
import Zoom from 'react-reveal/Zoom';
import Slide from 'react-reveal/Slide';
import { Rule } from '@mui/icons-material';
export const stacks = [
  {
    id: 0,
    icon: reactlogo, // 'https://images.unsplash.com/photo-1537944434965-cf4679d1a598?auto=format&fit=crop&w=400&h=250&q=60',
  },
  {
    id: 1,
    icon: reactNativelogo,
  },
  {
    id: 2,
    icon: reduxlogo,
  },
 
];

export default function Contact() {
  const theme = useTheme();
  const isSxMatch = useMediaQuery(theme.breakpoints.down("xs"));
  const isSmMatch = useMediaQuery(theme.breakpoints.down("sm"));
  const isMdMatch = useMediaQuery(theme.breakpoints.down("md"));
  const isLgMatch = useMediaQuery(theme.breakpoints.down("lg"));
  const email = useRef()
    const password = useRef()

  const[value,setValue]=useState({
    name:'',
    email:'',
    subject:'',
    message:'',

  })
  const[errorGet,setErrorGet]=useState({
    name:false,
    email:false,
    subject:false,
    message:false,
    emailError:false,

  })
  const onSubmit = (event) => {
    // event.preventDefault()
    console.log(email.current.value,'getthevalue') 
  };


 const  emailValidation=(value)=>{
  // alert('second')
  // console.log(value,'getthevalue')
    const regex = /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
    if(value || regex.test(value) === false){
      console.log('false')
      setErrorGet(previousInputs => ({ ...previousInputs,email:true, emailError: true }))
    }else{
    console.log('true')
    setErrorGet(previousInputs => ({ ...previousInputs,email:false, emailError: false }))
    }
}
  const emailChange= async (e)=>{
    // alert('fisrt')
    await emailValidation(e.target.value)
    setValue(previousInputs => ({ ...previousInputs, name: e.target.value }))

  }
  const {
    register,
    handleSubmit,
    getValues,
    formState: { errors },
  } = useForm();
  return (
    <Container maxWidth={false} disableGutters sx={{ background: "#303131", 
    // backgroundImage: `url(${headerjpg})`
     }}>
    <Container maxWidth={false} disableGutters 
    // sx={{ backgroundImage: `url(${hire})`, backgroundAttachment: 'fixed' }}
    >
        <Container sx={{ paddingTop: 5, paddingBottom: 5 }}>

          <Fade left>
            <Box sx={{ typography: isLgMatch ? 'h5' : 'h3', color: '#fff', textAlign: 'center', paddingBottom: 10 }} >Work Expertise</Box>
          </Fade>{/* <Time/> */}
        
          
            <Box sx={{
              flexGrow: 1,
            }}>  <Grid container spacing={2} className='checkGriud'
                 
              >



                <Grid container spacing={0}

                  className='expertise'
                  direction="column" item xs={12} lg={4} sm={6} md={6}
                  sx={{ display: 'flex', }}>
                    <Slide bottom >
                  <Box >
                    <Box sx={{
                    }}>
            <Box sx={{ fontSize:26, color: '#ccc',  }} >
             Get in touch</Box>
  <Box  sx={{flexDirection:'row',paddingTop:3}}> 
           <Box  sx={{display:'flex',marginBottom:3}}> 
           <Box sx={{ typography:'p', color: '#727b86',fontWeight:'bold' }}>Address : </Box>
           <Link sx={{color:'#727b86',ypography:'p',marginLeft:1}} target={'blank'} href="https://goo.gl/maps/swBcXk3FvtoFgmfF9" underline="hover">
  {'252, Harveypatti,Madurai'}
</Link></Box>
<Box  sx={{display:'flex',marginBottom:3}}> 
<Box sx={{ typography:'p', color: '#727b86',fontWeight:'bold' }}>Email : </Box>
           <Link sx={{color:'#727b86', typography:'p',marginLeft:1}} target={'blank'} href="https://mail.google.com/mail/?view=cm&fs=1&to=jk23arunkumar@gmail.com&su=SUBJECT&body=BODY&bcc=jk23arunkumar@gmail.com" underline="hover">
  {'jk23arunkumar@gmail.com'}
</Link></Box>

<Box  sx={{display:'flex',marginBottom:3}}> 
<Box sx={{ typography:'p', color: '#727b86',fontWeight:'bold' }}>Contact : </Box>
           <Link sx={{color:'#727b86', typography:'p',marginLeft:1}} target={'blank'} href="tel:+91 9994334040" underline="hover">
  {'9994334040'}
</Link></Box></Box>
                      </Box>
                  </Box>
                  </Slide>
                </Grid>
               
                <Grid container spacing={0}

                  className='contactDiv'
                  direction="column" item xs={12} lg={8} sm={6} md={6}
                  sx={{ display: 'flex' }}>
              <Zoom >
              <form onSubmit={handleSubmit(onSubmit)}>
              <TextField
               
              color="warning"
              sx={{width:'100%'}}
              id="outlined-basic" 
              label="Name"
              fullWidth
              autoComplete="Name"
              autoFocus
              {...register("Name", { required: true })}
              margin="normal"
              error={!!errors?.Name}
              helperText={errors?.Name ? "Name is required" : null}
              variant="outlined"
              
        />
        <TextField
         color="warning"
        variant="outlined"
        label="Email"
        id="outlined-basic"
        fullWidth
        autoComplete="Email"
        autoFocus
        margin="normal"
        {...register("Email", {
          required: "Required field",
          pattern: {
            value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
            message: "Invalid email address",
          },
        })}
        error={!!errors?.Email}
        helperText={errors?.Email ? errors.Email.message : null}
             
        />
        <TextField
         inputRef={email} 
        color="warning"
        sx={{width:'100%'}}
        id="outlined-basic" 
        label="Subject"
        fullWidth
        autoComplete="Subject"
        autoFocus
        {...register("Subject", { required: true, })}
        margin="normal"
        error={!!errors?.Subject}
        helperText={errors?.Subject ? "Subject is required" : null}
        variant="outlined"
        />
        <TextField
         inputRef={password}
         color="warning"
         sx={{width:'100%'}}
         id="outlined-basic" 
         label="Message"
         fullWidth
         autoComplete="Message"
         autoFocus
         multiline
         rows={4}
         {...register("Message", { required: true,  })}
         margin="normal"
         error={!!errors?.Message}
         helperText={errors?.Message ? "Message is required" : null}
         variant="outlined"
        />
         <Button
         
          href={`mailto:email@example.com?subject=${getValues("Sub")}&body= ${password.current.value}`}  
          type="submit" className='buttonProject' variant="contained" >Submit</Button></form>
                     </Zoom>
                    
                </Grid>
              </Grid>
            </Box>


        </Container>

      </Container></Container>
  );
}
