import * as React from 'react';
import {Box,Container,Grid,useMediaQuery} from '@mui/material';
import { useTheme } from '@mui/material/styles';
import MobileStepper from '@mui/material/MobileStepper';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import KeyboardArrowLeft from '@mui/icons-material/KeyboardArrowLeft';
import KeyboardArrowRight from '@mui/icons-material/KeyboardArrowRight';
import SwipeableViews from 'react-swipeable-views';
import { autoPlay } from 'react-swipeable-views-utils';
import { steps } from '../utils/commonHelper';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepButton from '@mui/material/StepButton';
import Fade from 'react-reveal/Fade';
import Time from './time';
const AutoPlaySwipeableViews = autoPlay(SwipeableViews);



export default function TextMobileStepper() {
  const theme = useTheme();
  const isSxMatch = useMediaQuery(theme.breakpoints.down("xs"));
  const isSmMatch = useMediaQuery(theme.breakpoints.down("sm"));
  const isMdMatch = useMediaQuery(theme.breakpoints.down("md"));
  const isLgMatch = useMediaQuery(theme.breakpoints.down("lg"));
  const [activeStep, setActiveStep] = React.useState(0);
  const [completed, setCompleted] = React.useState({});
  const maxSteps = steps.length;
  const totalSteps = () => {
    return steps.length;
  };

  const completedSteps = () => {
    return Object.keys(completed).length;
  };

  const isLastStep = () => {
    return activeStep === totalSteps() - 1;
  };

  const allStepsCompleted = () => {
    return completedSteps() === totalSteps();
  };
  const handleNext = () => {
    const newActiveStep =
      isLastStep() && !allStepsCompleted()
        ? // It's the last step, but not all steps have been completed,
          // find the first step that has been completed
          steps.findIndex((step, i) => !(i in completed))
        : activeStep + 1;
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };
  const handleStep = (step) => () => {
    setActiveStep(step);
  };

  const handleComplete = () => {
    const newCompleted = completed;
    newCompleted[activeStep] = true;
    setCompleted(newCompleted);
    handleNext();
  };
  const handleStepChange = (step) => {
    setActiveStep(step);
  };
  const handleReset = () => {
    setActiveStep(0);
    setCompleted({});
  };
  return (
    <Container maxWidth={false} disableGutters sx={{ background:"#303131"}}>
 <Container  sx={{paddingTop:10}}>
 <Box sx={{ width: '100%' }}>
      <Stepper nonLinear activeStep={activeStep}>
        {steps.map((labe, index) => (
          <Step key={label1} completed={completed[index]}>
            <StepButton color="inherit" onClick={handleStep(index)}>
              {label1}
            </StepButton>
          </Step>
        ))}
      </Stepper>
      <div>
        {allStepsCompleted() ? (
          <React.Fragment>
            <Typography sx={{ mt: 2, mb: 1 }}>
              All steps completed - you&apos;re finished
            </Typography>
            <Box sx={{ display: 'flex', flexDirection: 'row', pt: 2 }}>
              <Box sx={{ flex: '1 1 auto' }} />
              <Button onClick={handleReset}>Reset</Button>
            </Box>
          </React.Fragment>
        ) : (
          <React.Fragment>
            <Typography sx={{ mt: 2, mb: 1, py: 1 }}>
              Step {activeStep + 1}
            </Typography>
            <Box sx={{ display: 'flex', flexDirection: 'row', pt: 2 }}>
              <Button
                color="inherit"
                disabled={activeStep === 0}
                onClick={handleBack}
                sx={{ mr: 1 }}
              >
                Back
              </Button>
              <Box sx={{ flex: '1 1 auto' }} />
              <Button onClick={handleNext} sx={{ mr: 1 }}>
                Next
              </Button>
              {activeStep !== steps.length &&
                (completed[activeStep] ? (
                  <Typography variant="caption" sx={{ display: 'inline-block' }}>
                    Step {activeStep + 1} already completed
                  </Typography>
                ) : (
                  <Button onClick={handleComplete}>
                    {completedSteps() === totalSteps() - 1
                      ? 'Finish'
                      : 'Complete Step'}
                  </Button>
                ))}
            </Box>
          </React.Fragment>
        )}
      </div>
    </Box>
 <Fade left>
 <Box sx={{ typography: isLgMatch?'h5':'h3', color: '#fff',textAlign: 'center',paddingBottom:10 }} >Projects</Box>
  </Fade>{/* <Time/> */}
  <Fade right>
 <Box sx={{
                    flexGrow: 1,  
                }}>
                    
                    <Grid container spacing={2}>
                     
                        <Grid  container spacing={0}
                            direction="column" item xs={12} lg={6} sx={{placeSelf: 'flex-start',display:'flex', }}>
                               <MobileStepper 
                               sx={{background: '#f5af31'}}
        variant="text"
        steps={maxSteps}
        position="static"
        activeStep={activeStep}
        nextButton={
          <Button
            size="small"
            onClick={handleNext}
            disabled={activeStep === maxSteps - 1}
            sx={{color:'#fff'}}
          >
            {/* Next */}
            {theme.direction === 'rtl' ? (
              <KeyboardArrowLeft />
            ) : (
              <KeyboardArrowRight />
            )}
          </Button>
        }
        backButton={
          <Button size="small" onClick={handleBack} sx={{color:'#fff'}} disabled={activeStep === 0}>
            {theme.direction === 'rtl' ? (
              <KeyboardArrowRight />
            ) : (
              <KeyboardArrowLeft />
            )}
            {/* Back */}
          </Button>
        }
      /> 
                              <Box sx={{ width:'100%'}}>
                            <SwipeableViews
        axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
        index={activeStep}
        onChangeIndex={handleStepChange}
        enableMouseEvents
      >
        {steps.map((step, index) => (
          <div key={step.label}>
            {Math.abs(activeStep - index) <= 2 ? (
                <>
              <Box
                component="img"
                sx={{
                  width:'99%',
                  border: '3px solid #f5af31',
                }}
                src={step.imgPath}
                alt={step.label}
              />
              <Box sx={{  maxWidth: 500, width: '100%',paddingTop:2 ,color:'#a8a8a8'}}>
              {step.description1}
            </Box></>
            ) : null}
            
          </div>
        ))}
        
      </SwipeableViews></Box>

                        </Grid>
                        <Grid container
                            spacing={0}
                            direction="column"
                            style={{
                                justifyContent: "center", alignContent: 'center',
                            }} item xs={12} lg={6}>
                                
                                <Box sx={{ maxWidth: 400, flexGrow: 1 }}>
      <Paper
        square
        elevation={0}
        sx={{
          display: 'flex',
          bgcolor: 'transparent',
          paddingBottom:2
        }}
      >
        <Typography  sx={{ typography: isLgMatch?'h6':'h5', color: '#fff',paddingLeft:0 }}   >{steps[activeStep].label1}</Typography>
      </Paper>
      <Box sx={{ maxWidth: 400, width: '100%',color:'#a8a8a8'  }}>
        {steps[activeStep].description2}</Box>
        <Typography sx={{paddingTop:2,paddingBottom:2,color:'#fff'}}>{steps[activeStep].label3}</Typography>
      <Box sx={{ maxWidth: 400, width: '100%',color:'#a8a8a8' }}>
        {steps[activeStep].description3}
      </Box>

    </Box>    </Grid>



                    </Grid>
                   
                </Box></Fade>
   </Container></Container>
  );
}
