import React,{useRef,createRef} from 'react';
import Navbar from './Navbar';
import Header from './Header';
import AboutMe from './AboutMe';
import Projects from './Projects';
import Timeline from './Timeline';
import Expertise from './Expertise';
import Contact from './Contact';
import Footer from './Footer';
import {Element, Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'
const Home = () => {
    const service = createRef();;
    const works = useRef(null);
    const contact = useRef(null);
    const scrollToSection= (elementRef) =>{
        elementRef.current.scrollIntoView({ behavior: 'smooth' });
        // window.scrollTo({
        //     top:elementRef.current.offsetTop,
        //     behavior:"smooth",
        // });
    };  

    // const serviceGo = scrollToSection(service)
    // const worksGo = scrollToSection(works)
    // const contactGo = scrollToSection(contact)
    return (
        <React.Fragment>
            
            <Navbar   service={service} 
            works={works} contact={contact}
            />
            <Header/>
            <AboutMe/>
            <Timeline  id="timline" ref={service}/>
            <Projects  id="projects"ref={works}/>
            <Expertise  id="expertise"/>
            <Contact  id="contact"/>
            <Footer  id="footer"/>
       </React.Fragment>
    );
};

export default Home;
