import React from 'react';
import { Container, Grid, Box, styled, Paper, CardMedia, useMediaQuery, useTheme, Button } from '@mui/material';
import innerjpg from '../assets/stackBg.png'
import Rotate from 'react-reveal/Rotate';
import Fade from 'react-reveal/Fade';
import stackBg from '../assets/about_shap_bg.png'


function AboutMe(props) {
    const theme = useTheme();
    const isSxMatch = useMediaQuery(theme.breakpoints.down("xs"));
    const isSmMatch = useMediaQuery(theme.breakpoints.down("sm"));
    const isMdMatch = useMediaQuery(theme.breakpoints.down("md"));
    const isLgMatch = useMediaQuery(theme.breakpoints.down("lg"));

    let fontSize
    return (
        <React.Fragment>

            <Container maxWidth={false} disableGutters sx={{ backgroundColor: '#303131' }}>
                <Container>
                <Box sx={{
                    flexGrow: 1,
                    paddingTop: "20%" ,paddingTop:isLgMatch?'15%':'7%',paddingBottom:'5%',
                    
                }}>
                    
                    <Grid container spacing={2}>
                        <Grid spacing={0}
                            direction="column" item xs={12} lg={6} sx={{backgroundImage: `url(${stackBg})`,
                            backgroundSize:'cover' ,placeSelf: 'center',display:'flex',paddingTop:isSmMatch?30:0 }}>
                            <Box sx={{
                                margin: '0px auto',
                                width: isSmMatch?'70%':'25%',
                                border: '4px solid #f5af31',
                                padding: '10px 10px 20px 25px',
                            }}>
                                 <Rotate top left>
                                <CardMedia
                                    component="img"
                                    sx={{
                                        width: isLgMatch ? '100%' : 'auto',
                                        objectFit: isLgMatch?'fill':'cover',
                                        height:  isLgMatch ? 'auto' : 350,
                                        border: '10px solid #444',
                                        marginTop: -5,
                                    
                                    }}
                                image={isLgMatch ? innerjpg : innerjpg}
                                alignItems='right'
                                alt="Live from space album cover"
              /></Rotate></Box>
                        </Grid>
                        <Grid container
                            spacing={0}
                            direction="column"
                            textAlign={isLgMatch ? 'center' : 'left'}
                            style={{
                                justifyContent: "center", padding: "2% 2%", alignContent: 'center',
                                alignItems: isLgMatch ? 'center' : 'auto'
                            }} item xs={12} lg={6}>
                                <Fade top>
                                 <Box sx={{ typography: isLgMatch?'h5':'h3', color: '#fff' }} classNa >About Me</Box></Fade>
                                 <Fade bottom>   <Box sx={{ typography: 'p', color: '#a8a8a8',marginTop:2 }}>I am skilled in React Native , React.Js , Javascript , HTML , CSS  have worked with both web-based, multi-threaded applications as well as front-end development using the latest web technologies and good in developing customer service-oriented applications with solid test automation strategies.

In past have worked on both service and project side in service projects utilizing  Blockchain technologies like Trade , Swap ,Liquidity , stake , NFT and in product game platform like Dream11 ,e-commerce moreover in React Native educational based app for students ,music app like Gaana.

</Box> <Box sx={{ typography: 'p', color: '#a8a8a8',marginTop:2 }}>
In addition to my skills in coding, I am skilled in conducting technical analysis as well as assisting clients in project management.</Box>

<Box sx={{ typography: 'p', color: '#a8a8a8',marginTop:2 }}>Software Engineer | React Developer | React Native Developer | Web Designer , Web Developer</Box>
                    </Fade>    </Grid>



                    </Grid>
                </Box>
                </Container>
            </Container>

        </React.Fragment>
    );
}

export default AboutMe;
