import React, { useState } from "react";
import {
  AppBar,
  Tab,
  Tabs,
  Toolbar,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import AddBusinessRoundedIcon from "@mui/icons-material/AddBusinessRounded";
import DrawerComp from "./Drawer";
import {Link} from 'react-scroll'
const Navbar = ({service,works,contatc}) => {
  console.log(service,'serices')
  const [value, setValue] = useState();
  const theme = useTheme();
  console.log(theme);
  const isMatch = useMediaQuery(theme.breakpoints.down("md"));
  console.log(isMatch);
  const [colorChange, setColorchange] = useState(false);
  const changeNavbarColor = () =>{
     if(window.scrollY >= 80){
       setColorchange(true);
     }
     else{
       setColorchange(false);
     }
  };
  // window.addEventListener('scroll', changeNavbarColor);
  //    const scrollToSection= (elementRef) =>{
  //     alert('hi')
  //     elementRef.current.scrollIntoView({ behavior: 'smooth' });
  //       window.scrollTo({
  //           top:elementRef.current.offsetTop,
  //           behavior:"smooth",
  //       });
  //   };  
  return (
    <React.Fragment>
      <AppBar className={colorChange?"backGroundAdd":'backGroundRemove'}>
        <Toolbar >
          <AddBusinessRoundedIcon sx={{ transform: "scale(2)" }} />
          {isMatch ? (
            <>
              <DrawerComp />
            </>
          ) : (
            <>
              <Tabs
                sx={{ marginLeft: "auto" }}
                indicatorColor="secondary"
                textColor="inherit"
                value={value}
                onChange={(e, value) => setValue(value)}
              >
                  <Link className="test6" to="timeline" spy={true} smooth={true} duration={500}>
                 <Tab  label="Services" /> </Link>
                <Tab label="Works" />
                <Tab label="About Us" />
                <Tab label="Contact" />
              </Tabs>
            </>
          )}
        </Toolbar>
      </AppBar>
    </React.Fragment>
  );
};

export default Navbar;
