// Photos from https://citizenofnowhe.re/lines-of-the-city
import "./project.css"
import { useRef } from "react"
import { motion, useScroll, useSpring, useTransform } from "framer-motion"
import { steps } from "../utils/commonHelper"
function useParallax(value, distance) {
  return useTransform(value, [0, 1], [-distance, distance])
}

function Image({ id }) {
  const ref = useRef(null)
  const { scrollYProgress } = useScroll({ target: ref })
  const y = useParallax(scrollYProgress, 300)

  return (
    <div>
    <section className="projectSection">
      <div className="projectDiv"ref={ref}>
        <img  className="projectImg" src={id} alt="A London skyscraper" />
      </div>
      <motion.h2 style={{ y }}>{`#00${id}`}</motion.h2>
    </section></div> 
  )
}

export default function ProjectParal() {
  const { scrollYProgress } = useScroll()
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  })

  return (
    <>
       {steps.map((label, index) => (
        <Image id={label.imgPath} />
      ))}
      <motion.div className="projectProgress" style={{ scaleX }} />
    </>
  )
}
